// import { hasMetadata } from 'core-js/fn/reflect'
import { createStore } from 'vuex'

export default createStore({
  state: {
    names: [
      {
        id: 1,
        name: "rahul",

      }, {
        id: 2,
        name: "drishit",

      }, {
        id: 3,
        name: "ved",

      }, {
        id: 4,
        name: "het",

      },
    ]
  },
  getters: {
    allNames: state => state.names,
  },
  mutations: {
    newName: (state, userInfo) => {
      state.names = [userInfo, ...state.names]
    },
    deleteName: (state, id) => {
      state.names = state.names.filter(name => name.id != id)
    },
    editStateUser: (state, data) => {
      // state.names = name
      console.log(data);
      state.names.forEach((el, index) => {
        console.log('el -->', el, index);
        if (el.id === data.editId) {
          state[index].names
        }
      })

    }
  },
  actions: {
    addName: (context, userInfo) => {
      context.commit("newName", userInfo)

    },
    removeName: (context, id) => {
      context.commit("deleteName", id)
    },
    updateStateUser: (context, name) => {
      context.commit("editStateUser", name)
    }
  },
  modules: {
  }
})
